import React, { useState, useEffect } from "react";
import { supabase } from "../supabaseClient";
import { v4 as uuidv4 } from "uuid";

const palette = [
  "#3B82F6", // indigo/albastru (tema birou)
  "#6366F1",
  "#0EA5E9",
  "#14B8A6",
  "#F59E0B",
  "#EF4444",
];

const Proiecte = () => {
  // formular proiect
  const [form, setForm] = useState({
    nume_client: "",
    beneficiar: "",
    domiciliu: "",
    amplasament: "",
    titlu_proiect: "",
    data_predare: "",
    responsabil: "",
    stare: "neînceput",
    culoare_proiect: "",
    servicii: [],
    cost_total: 0,
    avans: 0,
    rest_plata: 0,
  });

  // taskuri generate automat din servicii
  const [taskuri, setTaskuri] = useState([]);

  // responsabili (colegii tăi din tabelul `utilizatori`)
  const [listaResponsabili, setListaResponsabili] = useState([]);

  // istoric modificări (chat intern proiect)
  const [istoric, setIstoric] = useState([
    // exemplu inițial; la salvare vom trimite în Supabase
    {
      id: uuidv4(),
      autor: "radu",
      actiune: "Creat proiect nou",
      data: new Date().toLocaleString("ro-RO"),
    },
  ]);
  const [notaNoua, setNotaNoua] = useState("");

  // alegem o culoare unică pentru proiect o singură dată
  useEffect(() => {
    const color = palette[Math.floor(Math.random() * palette.length)];
    setForm((prev) => ({ ...prev, culoare_proiect: color }));
  }, []);

  // aducem lista de responsabili din Supabase (tabelul "utilizatori")
  useEffect(() => {
    const fetchResponsabili = async () => {
      const { data, error } = await supabase
        .from("utilizatori")
        .select("nume");
      if (!error && data) {
        // ex: [{ nume: "Radu" }, { nume: "Dani" }]
        setListaResponsabili(data.map((r) => r.nume));
      }
    };
    fetchResponsabili();
  }, []);

  // serviciile standard pe care le oferiți
  const listaServicii = [
    { id: "cu", nume: "Certificat de urbanism", pret: 800, icon: "🧾" },
    { id: "dtac", nume: "Documentație tehnică (DTAC)", pret: 2500, icon: "🏗️" },
    { id: "dtad", nume: "Documentație tehnică (DTAD)", pret: 1500, icon: "🏘️" },
    { id: "avize", nume: "Obținere avize", pret: 1200, icon: "📄" },
    { id: "expertiza", nume: "Expertizare tehnică", pret: 1000, icon: "🧱" },
    { id: "releeve", nume: "Releveu", pret: 600, icon: "📏" },
  ];

  // schimbare stare proiect (neînceput / în lucru / finalizat)
  function setStareNoua(stareNoua) {
    setForm((prev) => ({ ...prev, stare: stareNoua }));

    adaugaInIstoric(
      `Starea proiectului a devenit "${stareNoua}"`,
      "sistem"
    );
  }

  // helper: adaugă linie în istoric local și (mai târziu) în Supabase
  function adaugaInIstoric(textActiune, autor = "radu") {
    const intrare = {
      id: uuidv4(),
      autor,
      actiune: textActiune,
      data: new Date().toLocaleString("ro-RO"),
    };
    setIstoric((prev) => [intrare, ...prev]);
    // TODO: salvare în tabelul "istoric_proiecte" din Supabase
  }

  // selectare/deselectare serviciu
  const toggleServiciu = (serviciu) => {
    let updated;
    if (form.servicii.includes(serviciu.id)) {
      updated = form.servicii.filter((s) => s !== serviciu.id);
    } else {
      updated = [...form.servicii, serviciu.id];
    }

    // recalculează cost total
    const total = updated.reduce((sum, id) => {
      const s = listaServicii.find((x) => x.id === id);
      return sum + (s?.pret || 0);
    }, 0);

    // update form financiar
    setForm((prev) => ({
      ...prev,
      servicii: updated,
      cost_total: total,
      rest_plata: total - prev.avans,
    }));

    // generează taskuri automat din serviciile bifate
    const generateTasks = updated.map((id) => ({
      id: uuidv4(),
      nume_task: listaServicii.find((x) => x.id === id)?.nume || "",
      status: "în lucru",
      termen: form.data_predare || "",
      culoare: form.culoare_proiect,
      responsabil_task: prevResponsabilTask(form.responsabil),
    }));

    setTaskuri(generateTasks);

    adaugaInIstoric(
      `Serviciile proiectului au fost actualizate (${updated.length} servicii selectate)`,
      "sistem"
    );
  };

  // fallback responsabil pentru taskuri: responsabilul proiectului
  function prevResponsabilTask(responsabilProiect) {
    if (!responsabilProiect || responsabilProiect === "") return "NE-ATRIBUIT";
    return responsabilProiect;
  }

  // când se modifică inputurile din form
  const handleChange = (e) => {
    const { name, value } = e.target;
    let newForm = { ...form, [name]: value };

    // dacă modifică avans => recalculează rest
    if (name === "avans") {
      const avansVal = parseFloat(value || 0);
      newForm.avans = avansVal;
      newForm.rest_plata = newForm.cost_total - avansVal;
    }

    // dacă schimbă responsabilul
    if (name === "responsabil") {
      // actualizăm și pe taskuri responsabilul implicit
      setTaskuri((prev) =>
        prev.map((t) => ({
          ...t,
          responsabil_task: value || "NE-ATRIBUIT",
        }))
      );
    }

    setForm(newForm);
  };

  // adaugă o notă nouă în istoric (chat intern)
  function handleAdaugaNota() {
    if (!notaNoua.trim()) return;
    adaugaInIstoric(notaNoua.trim(), "radu"); // aici putem pune userul logat
    setNotaNoua("");
  }

  // salvare proiect + taskuri în Supabase
  async function handleSave() {
    // 1. salvăm proiectul în tabelul `clienti`
    const { data, error } = await supabase
      .from("clienti")
      .insert([
        {
          nume_client: form.nume_client,
          beneficiar: form.beneficiar,
          domiciliu: form.domiciliu,
          amplasament: form.amplasament,
          titlu_proiect: form.titlu_proiect,
          data_predare: form.data_predare,
          responsabil: form.responsabil,
          stare: form.stare,
          culoare_proiect: form.culoare_proiect,
          servicii: form.servicii,
          cost_total: form.cost_total,
          avans: form.avans,
          rest_plata: form.rest_plata,
        },
      ])
      .select("id");

    if (error) {
      alert("Eroare la salvare proiect!");
      console.error(error);
      return;
    }

    const clientId = data?.[0]?.id;

    // 2. salvăm taskurile în tabelul `taskuri`
    if (clientId && taskuri.length > 0) {
      const taskuriDeSalvare = taskuri.map((t) => ({
        client_id: clientId,
        nume_task: t.nume_task,
        status: t.status,
        termen: t.termen,
        culoare: t.culoare,
        responsabil: t.responsabil_task,
      }));

      const { error: taskErr } = await supabase
        .from("taskuri")
        .insert(taskuriDeSalvare);

      if (taskErr) {
        console.error(taskErr);
      }
    }

    // 3. salvăm istoric inițial (NOTĂ: necesită tabelul istoric_proiecte în Supabase)
    // aici doar pregătim logica
    // poți crea ulterior tabelul `istoric_proiecte` cu coloane:
    // id (uuid), client_id (uuid), autor (text), actiune (text), data (text)
    // și apoi faci insert cu supabase.from("istoric_proiecte").insert([...])

    alert("Proiectul și taskurile au fost salvate ✅");
    adaugaInIstoric("Proiect salvat în baza de date", "sistem");
  }

  // culoare stare (roșu / galben / verde)
  function colorStare() {
    if (form.stare === "neînceput") return "bg-red-600";
    if (form.stare === "în lucru") return "bg-yellow-400 text-black";
    if (form.stare === "finalizat") return "bg-green-600";
    return "bg-gray-400";
  }

  return (
    <div className="min-h-screen bg-gray-100 p-6 flex flex-col gap-6">

      {/* HEADER PROIECT */}
      <div
        className="rounded-2xl shadow-md p-5 text-white flex flex-col md:flex-row md:items-center md:justify-between"
        style={{ backgroundColor: form.culoare_proiect || "#3B82F6" }}
      >
        <div>
          <div className="text-sm opacity-80">Proiect nou</div>
          <div className="text-2xl font-semibold leading-tight">
            {form.titlu_proiect || "Titlu proiect"}
          </div>
          <div className="text-sm mt-1">
            Client:{" "}
            <span className="font-medium">
              {form.nume_client || "—"}
            </span>
          </div>
        </div>

        <div className="flex gap-2 mt-4 md:mt-0">
          <button
            onClick={() => setStareNoua("neînceput")}
            className={
              "px-3 py-1 rounded-full text-sm font-medium " +
              (form.stare === "neînceput"
                ? "bg-red-600 text-white"
                : "bg-white/30")
            }
          >
            Neînceput
          </button>
          <button
            onClick={() => setStareNoua("în lucru")}
            className={
              "px-3 py-1 rounded-full text-sm font-medium " +
              (form.stare === "în lucru"
                ? "bg-yellow-300 text-black"
                : "bg-white/30 text-white")
            }
          >
            În lucru
          </button>
          <button
            onClick={() => setStareNoua("finalizat")}
            className={
              "px-3 py-1 rounded-full text-sm font-medium " +
              (form.stare === "finalizat"
                ? "bg-green-500 text-white"
                : "bg-white/30")
            }
          >
            Finalizat
          </button>
        </div>
      </div>

      {/* FORMULAR PRINCIPAL */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Card date client/proiect */}
        <div className="bg-white rounded-xl shadow p-5 border border-gray-200">
          <h2 className="text-lg font-semibold text-gray-800 mb-4">
            Date beneficiar & amplasament
          </h2>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="text-sm text-gray-600">Nume client</label>
              <input
                className="w-full border rounded-lg p-2 mt-1 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                name="nume_client"
                value={form.nume_client}
                onChange={handleChange}
                placeholder="ex: Popescu Ion"
              />
            </div>

            <div>
              <label className="text-sm text-gray-600">Beneficiar</label>
              <input
                className="w-full border rounded-lg p-2 mt-1 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                name="beneficiar"
                value={form.beneficiar}
                onChange={handleChange}
                placeholder="ex: Popescu Ion"
              />
            </div>

            <div className="md:col-span-2">
              <label className="text-sm text-gray-600">Domiciliu beneficiar</label>
              <input
                className="w-full border rounded-lg p-2 mt-1 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                name="domiciliu"
                value={form.domiciliu}
                onChange={handleChange}
                placeholder="ex: Str. Exemplu nr. 12, Oradea"
              />
            </div>

            <div className="md:col-span-2">
              <label className="text-sm text-gray-600">Amplasament</label>
              <input
                className="w-full border rounded-lg p-2 mt-1 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                name="amplasament"
                value={form.amplasament}
                onChange={handleChange}
                placeholder="ex: Str. Terenului nr. 4, extravilan ..."
              />
            </div>

            <div className="md:col-span-2">
              <label className="text-sm text-gray-600">Titlu proiect</label>
              <input
                className="w-full border rounded-lg p-2 mt-1 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                name="titlu_proiect"
                value={form.titlu_proiect}
                onChange={handleChange}
                placeholder="ex: Proiect casă P+1E"
              />
            </div>

            <div>
              <label className="text-sm text-gray-600">Data predare</label>
              <input
                type="date"
                className="w-full border rounded-lg p-2 mt-1 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                name="data_predare"
                value={form.data_predare}
                onChange={handleChange}
              />
            </div>

            <div>
              <label className="text-sm text-gray-600">Responsabil proiect</label>
              <select
                className="w-full border rounded-lg p-2 mt-1 bg-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
                name="responsabil"
                value={form.responsabil}
                onChange={handleChange}
              >
                <option value="">Alege responsabil</option>
                {listaResponsabili.map((nume) => (
                  <option key={nume} value={nume}>
                    {nume}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>

        {/* Card servicii și financiar */}
        <div className="space-y-6">
          <div className="bg-white rounded-xl shadow p-5 border border-gray-200">
            <h2 className="text-lg font-semibold text-gray-800 mb-4">
              Servicii selectate
            </h2>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
              {listaServicii.map((s) => (
                <label
                  key={s.id}
                  className={`flex items-start space-x-3 border rounded-lg p-3 cursor-pointer transition ${
                    form.servicii.includes(s.id)
                      ? "bg-indigo-50 border-indigo-400"
                      : "bg-white hover:bg-gray-50 border-gray-300"
                  }`}
                >
                  <input
                    type="checkbox"
                    className="mt-1"
                    checked={form.servicii.includes(s.id)}
                    onChange={() => toggleServiciu(s)}
                  />
                  <div className="flex-1">
                    <div className="font-medium text-gray-800 flex items-center gap-2">
                      <span>{s.icon}</span>
                      <span>{s.nume}</span>
                    </div>
                    <div className="text-sm text-gray-500">
                      {s.pret} RON
                    </div>
                  </div>
                </label>
              ))}
            </div>
          </div>

          <div className="bg-white rounded-xl shadow p-5 border border-gray-200">
            <h2 className="text-lg font-semibold text-gray-800 mb-4">
              Situație financiară
            </h2>

            <div className="grid grid-cols-3 gap-4">
              <div className="bg-gray-50 rounded-lg border p-3">
                <p className="text-sm text-gray-600">Cost total</p>
                <p className="text-xl font-bold text-gray-900">
                  {form.cost_total} RON
                </p>
              </div>

              <div className="bg-gray-50 rounded-lg border p-3">
                <p className="text-sm text-gray-600">Avans</p>
                <input
                  type="number"
                  className="w-full border rounded-lg p-2 mt-1 bg-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  name="avans"
                  value={form.avans}
                  onChange={handleChange}
                />
              </div>

              <div className="bg-gray-50 rounded-lg border p-3">
                <p className="text-sm text-gray-600">Rest de plată</p>
                <p className="text-xl font-bold text-red-600">
                  {form.rest_plata} RON
                </p>
              </div>
            </div>
          </div>

          <button
            onClick={handleSave}
            className="w-full py-3 rounded-lg bg-indigo-600 text-white font-semibold shadow hover:bg-indigo-700 transition"
          >
            💾 Salvează proiectul & taskurile
          </button>
        </div>
      </div>

      {/* ISTORIC / CHAT INTERN */}
      <div className="bg-white rounded-xl shadow p-5 border border-gray-200">
        <h2 className="text-lg font-semibold text-gray-800 mb-4">
          Istoric modificări / Notițe interne proiect
        </h2>

        <div className="flex gap-4 mb-4">
          <input
            className="flex-1 border rounded-lg p-2 focus:outline-none focus:ring-2 focus:ring-indigo-500"
            placeholder="Adaugă o notă internă (ex. am depus CU la primărie)"
            value={notaNoua}
            onChange={(e) => setNotaNoua(e.target.value)}
          />
          <button
            onClick={handleAdaugaNota}
            className="px-4 py-2 bg-gray-800 text-white rounded-lg hover:bg-black transition"
          >
            ➕ Adaugă
          </button>
        </div>

        <div className="max-h-48 overflow-y-auto border rounded-lg bg-gray-50 p-3 space-y-3">
          {istoric.map((entry) => (
            <div
              key={entry.id}
              className="bg-white border rounded-lg p-3 shadow-sm"
            >
              <div className="text-sm text-gray-500 flex justify-between">
                <span className="font-semibold text-gray-800">
                  {entry.autor}
                </span>
                <span>{entry.data}</span>
              </div>
              <div className="text-gray-800 text-[15px] mt-1">
                {entry.actiune}
              </div>
            </div>
          ))}
        </div>
      </div>

    </div>
  );
};

