import React, { useEffect, useState } from "react";
import "./Layout.css";
import { Link } from "react-router-dom";
import { supabase } from "./supabaseClient";

const Layout = ({ children }) => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    supabase.auth.getUser().then(({ data }) => setUser(data?.user));
  }, []);

  async function handleLogout() {
    await supabase.auth.signOut();
    window.location.href = "/login";
  }

  // extragem doar partea dinainte de @
  const userName = user?.email ? user.email.split("@")[0] : "";

  return (
    <div className="layout">
      <aside
        className="sidebar"
        style={{
          backgroundColor: "#1f2937", // gri √Ænchis elegant
          color: "#f9fafb", // text deschis
          display: "flex",
          flexDirection: "column",
          padding: "20px",
          minHeight: "100vh",
        }}
      >
        <h2 style={{ color: "#60a5fa", marginBottom: "15px" }}>Flavi Proiectare</h2>

        {user && (
          <div
            style={{
              background: "#374151",
              padding: "10px 14px",
              borderRadius: "6px",
              marginBottom: "20px",
              fontSize: "15px",
              color: "#f9fafb",
              textAlign: "center",
              fontWeight: "500",
            }}
          >
            üëã {userName.charAt(0).toUpperCase() + userName.slice(1)}
          </div>
        )}

        <nav style={{ flex: "1" }}>
          <ul style={{ listStyle: "none", padding: 0 }}>
            <li><Link style={linkStyle} to="/">üè† Dashboard</Link></li>
            <li><Link style={linkStyle} to="/proiecte">üìÅ Proiecte</Link></li>
            <li><Link style={linkStyle} to="/taskuri">üß± Taskuri</Link></li>
            <li><Link style={linkStyle} to="/setari">‚öôÔ∏è SetƒÉri</Link></li>
          </ul>
        </nav>

        <button
          onClick={handleLogout}
          style={{
            padding: "10px",
            background: "#ef4444",
            border: "none",
            color: "white",
            borderRadius: "5px",
            cursor: "pointer",
            width: "100%",
            fontWeight: "500",
            transition: "0.2s",
          }}
          onMouseOver={(e) => (e.target.style.background = "#dc2626")}
          onMouseOut={(e) => (e.target.style.background = "#ef4444")}
        >
          Ie»ôire
        </button>
      </aside>

      <main className="content">{children}</main>
    </div>
  );
};

// stilul comun pentru linkuri
const linkStyle = {
  display: "block",
  padding: "10px 12px",
  color: "#d1d5db",
  textDecoration: "none",
  borderRadius: "6px",
  marginBottom: "5px",
  fontWeight: "500",
  transition: "0.2s",
  background: "transparent",
};

export default Layout;
