import React, { useEffect, useState } from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import { supabase } from "./supabaseClient";

// Pagini
import Login from "./Login";
import Layout from "./Layout";
import Dashboard from "./pages/Dashboard";
import Proiecte from "./pages/Proiecte";
import Taskuri from "./pages/Taskuri";
import ClientView from "./pages/ClientView"; // âœ… noua paginÄƒ detaliu proiect

export default function App() {
  const [session, setSession] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // ðŸ”¹ verificÄƒ sesiunea curentÄƒ
    supabase.auth.getSession().then(({ data: { session } }) => {
      setSession(session);
      if (!session) navigate("/login");
    });

    // ðŸ”¹ ascultÄƒ schimbÄƒrile de autentificare
    const { data: listener } = supabase.auth.onAuthStateChange((_event, session) => {
      setSession(session);
      if (!session) navigate("/login");
    });

    return () => listener.subscription.unsubscribe();
  }, [navigate]);

  // ðŸ”¹ DacÄƒ nu e autentificat â†’ login
  if (!session) {
    return <Login />;
  }

  // ðŸ”¹ Layout principal (cu sidebar È™i rute)
  return (
    <Layout>
      <Routes>
        <Route path="/" element={<Dashboard />} />
        <Route path="/proiecte" element={<Proiecte />} />
        <Route path="/taskuri" element={<Taskuri />} />
        <Route path="/client/:id" element={<ClientView />} /> {/* âœ… detaliu proiect */}
      </Routes>
    </Layout>
  );
}
