import React, { useState } from "react";
import { supabase } from "./supabaseClient";

export default function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  async function handleLogin(e) {
    e.preventDefault();
    setError("");
    setLoading(true);

    // curƒÉ»õƒÉm spa»õii »ôi transformƒÉm √Æn litere mici
    const cleanUsername = username.trim().toLowerCase();

    console.log("üîç Caut username:", cleanUsername);

    // cƒÉutƒÉm utilizatorul √Æn tabel (ignor√¢nd litere mari/mici)
    const { data: users, error: userError } = await supabase
      .from("users")
      .select("email, username")
      .eq("username", cleanUsername);

    console.log("üì¶ Rezultat cƒÉutare:", users, "Eroare:", userError);

    if (userError || !users || users.length === 0) {
      setError("‚ùå Utilizator inexistent!");
      setLoading(false);
      return;
    }

    const userEmail = users[0].email;
    console.log("üìß Email gƒÉsit:", userEmail);

    const { error: loginError } = await supabase.auth.signInWithPassword({
      email: userEmail,
      password,
    });

    if (loginError) {
      console.error("Eroare autentificare:", loginError);
      setError("‚ùå ParolƒÉ gre»ôitƒÉ!");
    } else {
      window.location.href = "/";
    }

    setLoading(false);
  }

  return (
    <div
      style={{
        maxWidth: 400,
        margin: "120px auto",
        padding: 30,
        border: "1px solid #ddd",
        borderRadius: 10,
        boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
        background: "#fff",
      }}
    >
      <h2 style={{ textAlign: "center", marginBottom: 30 }}>
        üîê Autentificare Flavi Proiectare
      </h2>

      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Nume utilizator"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={inputStyle}
          required
        />

        <input
          type="password"
          placeholder="ParolƒÉ"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={inputStyle}
          required
        />

        <button
          type="submit"
          disabled={loading}
          style={{
            width: "100%",
            padding: 10,
            background: loading ? "#9ca3af" : "#1f2937",
            color: "white",
            border: "none",
            borderRadius: 5,
            cursor: "pointer",
          }}
        >
          {loading ? "Se conecteazƒÉ..." : "Autentificare"}
        </button>
      </form>

      {error && <p style={{ color: "red", marginTop: 15 }}>{error}</p>}
    </div>
  );
}

const inputStyle = {
  width: "100%",
  marginBottom: 15,
  padding: 10,
  border: "1px solid #ccc",
  borderRadius: 5,
};
