import React, { useState } from "react";
import { supabase } from "./supabaseClient";

export default function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  async function handleLogin(e) {
    e.preventDefault();
    setError("");

    const normalizedUsername = username.trim().toLowerCase();

    console.log("üîç Caut utilizator:", normalizedUsername);

    // cƒÉutƒÉm √Æn tabelul `users`, fƒÉrƒÉ diferen»õƒÉ √Æntre majuscule/minuscule
    const { data, error: fetchError } = await supabase
      .from("users")
      .select("id, username, email")
      .ilike("username", normalizedUsername);

    console.log("üì¶ Rezultat cƒÉutare:", data, "Eroare:", fetchError);

    if (fetchError) {
      setError("‚ö†Ô∏è Eroare la conexiunea cu baza de date.");
      return;
    }

    if (!data || data.length === 0) {
      setError("‚ùå Utilizator inexistent");
      return;
    }

    const user = data[0];

    // generƒÉm emailul automat, bazat pe username
    const email = `${user.username.toLowerCase()}@flavi.ro`;

    // autentificare √Æn Supabase Auth
    const { error: loginError } = await supabase.auth.signInWithPassword({
      email,
      password,
    });

    if (loginError) {
      console.error(loginError);
      setError("‚ùå ParolƒÉ incorectƒÉ sau cont inexistent");
      return;
    }

    // salvƒÉm userul logat local (pentru a-l afi»ôa √Æn Dashboard)
    localStorage.setItem("user", user.username);

    // redirec»õionƒÉm la Dashboard
    window.location.href = "/";
  }

  return (
    <div
      style={{
        maxWidth: 400,
        margin: "120px auto",
        padding: 30,
        border: "1px solid #ddd",
        borderRadius: 10,
        boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
        background: "#fff",
      }}
    >
      <h2 style={{ textAlign: "center", marginBottom: 30 }}>
        üîê Autentificare Flavi Proiectare
      </h2>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Utilizator (ex: radu)"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{
            width: "100%",
            marginBottom: 15,
            padding: 10,
            border: "1px solid #ccc",
            borderRadius: 5,
          }}
          required
        />
        <input
          type="password"
          placeholder="ParolƒÉ"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{
            width: "100%",
            marginBottom: 15,
            padding: 10,
            border: "1px solid #ccc",
            borderRadius: 5,
          }}
          required
        />
        <button
          type="submit"
          style={{
            width: "100%",
            padding: 10,
            background: "#1f2937",
            color: "white",
            border: "none",
            borderRadius: 5,
            cursor: "pointer",
          }}
        >
          Autentificare
        </button>
      </form>

      {error && <p style={{ color: "red", marginTop: 15 }}>{error}</p>}
    </div>
  );
}
