import React, { useState } from "react";
import { supabase } from "./supabaseClient";

export default function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  async function handleLogin(e) {
    e.preventDefault();
    setError("");
    setLoading(true);

    // 1Ô∏è‚É£ CƒÉutƒÉm email-ul asociat username-ului
    const { data: userRow, error: userError } = await supabase
      .from("users")
      .select("email")
      .eq("username", username)
      .single();

    if (userError || !userRow) {
      setError("‚ùå Utilizator inexistent!");
      setLoading(false);
      return;
    }

    // 2Ô∏è‚É£ Autentificare Supabase folosind email + parolƒÉ
    const { error: loginError } = await supabase.auth.signInWithPassword({
      email: userRow.email,
      password,
    });

    if (loginError) {
      console.error(loginError);
      setError("‚ùå ParolƒÉ gre»ôitƒÉ!");
    } else {
      window.location.href = "/";
    }

    setLoading(false);
  }

  return (
    <div
      style={{
        maxWidth: 400,
        margin: "120px auto",
        padding: 30,
        border: "1px solid #ddd",
        borderRadius: 10,
        boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
        background: "#fff",
      }}
    >
      <h2 style={{ textAlign: "center", marginBottom: 30 }}>
        üîê Autentificare Flavi Proiectare
      </h2>

      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Nume utilizator"
          value={username}
          onChange={(e) => setUsername(e.target.value.trim())}
          style={inputStyle}
          required
        />

        <input
          type="password"
          placeholder="ParolƒÉ"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={inputStyle}
          required
        />

        <button
          type="submit"
          disabled={loading}
          style={{
            width: "100%",
            padding: 10,
            background: loading ? "#9ca3af" : "#1f2937",
            color: "white",
            border: "none",
            borderRadius: 5,
            cursor: "pointer",
          }}
        >
          {loading ? "Se conecteazƒÉ..." : "Autentificare"}
        </button>
      </form>

      {error && <p style={{ color: "red", marginTop: 15 }}>{error}</p>}
    </div>
  );
}

const inputStyle = {
  width: "100%",
  marginBottom: 15,
  padding: 10,
  border: "1px solid #ccc",
  borderRadius: 5,
};
